INSERT INTO "news_detail_backup" ("news_id", "news_body", "insert_time", "update_time", "source_name", "news_author", "news_url", "news_title", "news_fetch_time", "news_site_name", "news_publish_time", "news_comments_num", "hashValue", "has_summary", "isActive", "source_type", "news_tag", "related_company_id", "main_related_company_id", "event_id", "neg_pos", "duplicate_num", "kpi_tag", "comments_num", "kpi_type", "usefull", "group_id", "auto_update_time", "page_tag", "TMSTAMP") VALUES (45573926, '　　2018年6月1日，山东地炼汽、柴
油价
