INSERT INTO "news_detail_backup" ("news_id", "news_body", "insert_time", "update_time", "source_name", "news_author", "news_url", "news_title", "news_fetch_time", "news_site_name", "news_publish_time", "news_comments_num", "hashValue", "has_summary", "isActive", "source_type", "news_tag", "related_company_id", "main_related_company_id", "event_id", "neg_pos", "duplicate_num", "kpi_tag", "comments_num", "kpi_type", "usefull", "group_id", "auto_update_time", "page_tag", "TMSTAMP") VALUES (45573590, '中国网财经6月1日讯 华夏上证 3-5 年期中高评级可质押信用债交易型开放式指数证券投资基金今日发布公告，增聘刘明宇为基金经理，与祝灿共同管理该基金，任职日期2018年5月30日。
