INSERT INTO "news_detail_backup" ("news_id", "news_body", "insert_time", "update_time", "source_name", "news_author", "news_url", "news_title", "news_fetch_time", "news_site_name", "news_publish_time", "news_comments_num", "hashValue", "has_summary", "isActive", "source_type", "news_tag", "related_company_id", "main_related_company_id", "event_id", "neg_pos", "duplicate_num", "kpi_tag", "comments_num", "kpi_type", "usefull", "group_id", "auto_update_time", "page_tag", "TMSTAMP") VALUES (45573528, '最受欢迎的竟是乔治
北京时间6月1日，据美媒体报道，在球员的签名鞋中，被现役球员穿的最多的是保罗-乔治的PG1，凯里-欧文的签名鞋3代和4代分列二、三位。
在NBA球员中，一般只有全明星甚至巨星级别才会得到赞助商的青睐，拥有为自己设计的专属签名球鞋。这些人在现役球员中人数并不算多，于是那些没有自己签名球鞋的现役球员，只能向我们这些普通球迷一样穿着其他球员的签名球鞋。
在本赛季的统计中，被现役球员穿的最多的球星签名鞋，并不属于詹姆斯或者库里哈登这些顶级超巨，而是乔治的1代签名鞋，共42人穿。欧文的签名鞋也十分受欢迎，三代和四代分别有32人和22人在穿。

詹姆斯和库里则并列第六，各有14人选择了他们的签名鞋。
（云彦）', '2018-06-01 09:35:35.000', '2018-06-01 09:35:35.000', NULL, NULL, 'http://sports.sina.com.cn/basketball/nba/2018-06-01/doc-ihcikcev9198819.shtml', '[体育]最受现役球员欢迎的球鞋排行 詹皇库里未进前5', '2018-06-01 09:35:34.0000000', '新浪财经', '2018-06-01 09:29:00.0000000', NULL, '4481437105373552233', 0, 1, 'news_spider', -1, NULL, -1, -1, NULL, 0, NULL, NULL, -1, 1, 22814104, '2018-06-01 09:35:35.0000000', '新浪体育>篮球-NBA>俄克拉荷马城雷霆', 0x003FF5923F);
