INSERT INTO "news_detail_backup" ("news_id", "news_body", "insert_time", "update_time", "source_name", "news_author", "news_url", "news_title", "news_fetch_time", "news_site_name", "news_publish_time", "news_comments_num", "hashValue", "has_summary", "isActive", "source_type", "news_tag", "related_company_id", "main_related_company_id", "event_id", "neg_pos", "duplicate_num", "kpi_tag", "comments_num", "kpi_type", "usefull", "group_id", "auto_update_time", "page_tag", "TMSTAMP") VALUES (45573513, '为推动安诺其大数码产业发展、布局色彩数码港规划、发挥协同效应，5月29日下午，数码印花相关领域专家、安诺其数码产业相关负责人齐聚一堂，召开数码产业发展研讨会。出席本次研讨会的有江南大学纺织服装学院院长付少海，国家“千人计划”特聘专家、前美国柯达高级首席科学家谢永林博士（任安诺其战略投资的苏州锐发打印技术有限公司董事长），著名台湾数码染料专家孙自揆（任安诺其数码事业部总工程师），安诺其数码科技公司总经理吴清和，安诺其数码终端公司总经理袁伟文，以及安诺其集团董事长兼总裁纪立军、副总裁兼董秘徐长进、副总裁董肇伟、染化事业部副总经理孙永胜、应用技术部部长吴冬。研讨会上，纪立军董事长总结近几年安诺其数码产业发展概况，阐述安诺其在数码产业链的整体布局，并提出整合集团优势资源，布局色彩数码港，推动整个数码产业链协同发展的整体构想。随后各位专家、各项目及相关负责人纷纷发言，从产业链布局、行业发展、市场需求、技术趋势等方面提出新的思路和建议。从国内市场到国际市场，从个性化需求引领到数码印花一站式定制，从设备、生产到内容创新，从国家级研发中心设立到高校设计大赛的举行，从行业人才的引进到整体商业模式的构建，各与会人员畅所欲言，讨论现场气氛热烈。通过此次研讨会，安诺其大数码产业的整体发展规划更加清晰，各项目、各资源之间的协同发展也越发紧密。', '2018-06-01 09:35:29.000', '2018-06-01 09:35:29.000', '安诺其视界', 'anoky2014', 'https://mp.weixin.qq.com/s?__biz=MjM5MDcxOTIxOA==&mid=2650933386&idx=1&sn=1531947e3b9a7e97295100df74547d51&chksm=bdb625178ac1ac013d96b248d0dc612088d065c2b20dd43b0ba9cd78bc2be65521731a5451d8&scene=0', '安诺其召开数码产业发展研讨会', '2018-06-01 09:35:15.0000000', 'anoky2014', '2018-06-01 00:00:00.0000000', NULL, '15281580367643431080', 0, 1, 'news_spider', -1, NULL, -1, -1, NULL, 0, NULL, NULL, -1, 1, 22814092, '2018-06-01 09:35:29.0000000', NULL, 0x003FF5923F);
