INSERT INTO "news_detail_backup" ("news_id", "news_body", "insert_time", "update_time", "source_name", "news_author", "news_url", "news_title", "news_fetch_time", "news_site_name", "news_publish_time", "news_comments_num", "hashValue", "has_summary", "isActive", "source_type", "news_tag", "related_company_id", "main_related_company_id", "event_id", "neg_pos", "duplicate_num", "kpi_tag", "comments_num", "kpi_type", "usefull", "group_id", "auto_update_time", "page_tag", "TMSTAMP") VALUES (45573503, '新浪美股讯 北京时间1日路透社称，德国财政部长奥拉夫·绍尔茨（Olaf Scholz）表示，美国对加拿大、欧盟和墨西哥征收钢铝关税的决定是在公然违背国际法。
绍尔茨在前往加拿大参加G7财长峰会的途中表示：“这对跨大西洋关系是糟糕的一天。这个单方面的决定是错误的，在我看来违反了国际法。”
他表示，欧盟将“有力而明智地”做出反应，在欧盟国家和欧盟伙伴之间进行协调。
绍尔茨表示，欧盟准备就关税问题与美国进行磋商，但只有在华盛顿批准永久豁免欧盟的钢铝关税后才会这样做。
