INSERT INTO "news_detail_backup" ("news_id", "news_body", "insert_time", "update_time", "source_name", "news_author", "news_url", "news_title", "news_fetch_time", "news_site_name", "news_publish_time", "news_comments_num", "hashValue", "has_summary", "isActive", "source_type", "news_tag", "related_company_id", "main_related_company_id", "event_id", "neg_pos", "duplicate_num", "kpi_tag", "comments_num", "kpi_type", "usefull", "group_id", "auto_update_time", "page_tag", "TMSTAMP") VALUES (45573495, '布瑞克观点：31日，鸡蛋现货震荡调整：目前临近端午节，鸡蛋现货价格存在一定支撑，但近期天气多变，可能短期会影响蛋价，建议养殖户即产即销，可适当淘汰老鸡。鸡蛋期货1809合约下跌明显，9月是传统的消费旺季，但2017年下半年至今补栏较多，且目前09合约已在4100元/500千克附近，建议投资者观望为宜。后期关注现货价格走势。
期市动态：31日鸡蛋成交量15.32万手，持仓21.85万手，较上个交易日减仓2260手。主力合约JD1809小幅下跌，收盘价4125元/500千克，较前一个交易日结算价下跌29元/500千克，环比下跌0.70%。成交量小幅减少，持仓量减少2508手至144802手。
现货价格：31日，全国鸡蛋均价为3.68元/斤，环比下跌0.01元/斤， 重庆地区大幅下跌，均价下跌至3.78元/斤，环比下跌0.05元/斤。主销区均价3.68元/斤，环比上个工作日持平；主产区均价3.50元/斤，环比下跌0.01元/斤。
重要消息：5月25日，农业农村部印发了《全国农业污染源普查方案》通知。全国将抽取约6.4万养户开展调查，规模养殖场入户调查全覆盖。
（责任编辑：DF120）', '2018-06-01 09:35:10.000', '2018-06-01 09:35:10.000', NULL, NULL, 'http://www.microbell.com/ecodetail_5783113.html', '鸡蛋现货弱势维稳 期货小幅下跌', '2018-06-01 09:35:16.0000000', '迈博汇金股票资讯网', '2018-06-01 09:22:00.0000000', NULL, '10891457827905926259', 0, 1, 'news_spider', -1, NULL, -1, -1, NULL, 0, NULL, NULL, -1, 1, 22813981, '2018-06-01 09:35:09.0000000', '首页>财经资讯>期货市场', 0x003FF5923F);
