INSERT INTO "news_detail_backup" ("news_id", "news_body", "insert_time", "update_time", "source_name", "news_author", "news_url", "news_title", "news_fetch_time", "news_site_name", "news_publish_time", "news_comments_num", "hashValue", "has_summary", "isActive", "source_type", "news_tag", "related_company_id", "main_related_company_id", "event_id", "neg_pos", "duplicate_num", "kpi_tag", "comments_num", "kpi_type", "usefull", "group_id", "auto_update_time", "page_tag", "TMSTAMP") VALUES (45573292, 'SMM 6月1日讯：隔夜伦铜开盘于6849美元/吨，盘初摸高6860美元/吨后小幅回落探低6833美元/吨。上午伦铜整体呈震荡上行走势越过日均线，午后摸高6873美元/吨，随后缓步回落至6854美元/吨。进入欧美时间后伦铜冲高6895.5美元/吨后整体呈震荡下行走势，探低6801美元/吨后跌势收止反抽，摸高6873美元/吨。随后沿6850美元/吨一线短暂整理，振幅不断收窄。尾盘小幅下挫，收于6828美元/吨，跌12美元/吨，成交减9210手至1.9万手，仓增2517手至31.9万手。
隔夜沪铜主力1807合约开盘于51230元/吨，盘初小幅下挫探低51020元/吨。随后空头不断离场，推动铜价向上攀升越过日均线，摸高51570元/吨后涨势收止。后沿51405元/吨一线震荡运行，尾盘多头离场，小幅下挫，收于51360元/吨，涨130元/吨，仓减4646手至17万手。
隔夜伦铜整体维持区间震荡格局，技术层面看目前仍处于均线组下方，KDJ趋于收敛。隔夜沪铜主力1807合约整体低开高走目前陷于均线之间，51500元/吨一线存在一定压力。预计今日伦铜6800~6870美元/吨，沪铜主力51250~51750元/吨。现货方面僵持格局或将延续，持货商挺价情绪无消减迹象，预计今日报贴水40元~升水20元/吨。
（责任编辑：DF120）', '2018-06-01 09:29:37.000', '2018-06-01 09:29:37.000', NULL, NULL, 'http://www.microbell.com/ecodetail_5783099.html', '铜市维持震荡 市场关注度一般', '2018-06-01 09:29:35.0000000', '迈博汇金股票资讯网', '2018-06-01 09:13:00.0000000', NULL, '2101671645816353567', 0, 1, 'news_spider', -1, NULL, -1, -1, NULL, 0, NULL, NULL, -1, 1, 22813471, '2018-06-01 09:29:37.0000000', '首页>财经资讯>期货市场', 0x003FF5923F);
