INSERT INTO "news_detail_backup" ("news_id", "news_body", "insert_time", "update_time", "source_name", "news_author", "news_url", "news_title", "news_fetch_time", "news_site_name", "news_publish_time", "news_comments_num", "hashValue", "has_summary", "isActive", "source_type", "news_tag", "related_company_id", "main_related_company_id", "event_id", "neg_pos", "duplicate_num", "kpi_tag", "comments_num", "kpi_type", "usefull", "group_id", "auto_update_time", "page_tag", "TMSTAMP") VALUES (45573091, '国家统计局最新数据显示，5月份制造业PMI为51.9%，高于上月和上年同期0.5个和0.7个百分点，为2017年10月以来的高点。
华泰宏观李超团队认为，工业数据或处于阶段性回暖阶段，预计将持续至三季度初，但全年下行的速度将是缓慢的。
5月份制造业PMI分项指数中，除了供应商配送时间指数略有下降之外，其他4个指数全部上升。其中生产指数和新订单指数上涨幅度在1个百分点左右，涨幅较大。原材料库存指数和从业人员指数小幅上升0.1个百分点，略有改善但都低于临界点，分别为49.6%和49.1%。
交通银行金融研究中心连平团队认为，多数指数上升，制造业扩张加快，表明经济运行的微观动能充足。
5月份主要原材料购进价格和出厂价格指数为56.7%和53%，分别比上个月上升3.7个和3个百分点，涨幅较大。
连平团队表示，国际大宗商品特别是原油价格上涨，对我国工业产业带来输入性通胀，是原材料购进价格上涨的重要原因。石油加工及炼焦业、黑色金属冶炼及压延加工业价格上涨最大。工业企业利润增长加快，市场需求回升，带动工业生产和销售，也是带动制造业供需两端价格上升的原因。
联讯证券首席宏观研究员李奇霖团队认为，两个出厂价格指数明显上涨，预计5月PPI同比继续回升至3.8%左右。
数据还显示，5月份反映资金紧张的企业比重为40.1%，连续3个月上升。在国家统计局服务业调查中心高级统计师赵庆河看来，这说明金融服务实体经济的支持力度仍需进一步增强。同时，反映原材料成本和劳动力成本高的企业比重持续超过四成，成本压力较大依然是企业生产经营的主要问题之一。', '2018-06-01 09:24:14.000', '2018-06-01 09:24:14.000', '上海证券报', NULL, 'https://www.howbuy.com/news/2018-06-01/5813526.html', '5月制造业PMI升至51.9% 工业经济或阶段性回暖', '2018-06-01 09:24:14.0000000', '好买基金网', '2018-06-01 09:09:00.0000000', NULL, '1670772658735297067', 0, 1, 'news_spider', -1, NULL, -1, -1, NULL, 0, NULL, NULL, -1, 1, 22811471, '2018-06-01 09:24:14.0000000', '宏观新闻', 0x003FF5923F);
