INSERT INTO "news_detail_backup" ("news_id", "news_body", "insert_time", "update_time", "source_name", "news_author", "news_url", "news_title", "news_fetch_time", "news_site_name", "news_publish_time", "news_comments_num", "hashValue", "has_summary", "isActive", "source_type", "news_tag", "related_company_id", "main_related_company_id", "event_id", "neg_pos", "duplicate_num", "kpi_tag", "comments_num", "kpi_type", "usefull", "group_id", "auto_update_time", "page_tag", "TMSTAMP") VALUES (45573072, '6月起，国家林业和草原局将在全国范围内集中开展自然保护地大检查，摸清各类自然保护地底数和管理薄弱环节，全面系统排查和预防整治自然保护地内破坏自然资源的违法违规情况，切实加强自然生态系统原真性、完整性、系统性保护，守住生态红线，筑牢国家生态安全屏障。
据了解，此次检查范围包括全国各级各类自然保护地，其中重点检查国家级自然保护区、国家级风景名胜区、国家级森林公园、国家湿地公园、国家地质公园、海洋特别保护区（海洋公园）、自然遗产等。检查将摸清各类自然保护地底数和现状，全面了解管理机构、四至边界、规划建设和各类自然保护地交叉重叠等情况，为下一步整合、优化和完善各类自然保护地提供决策参考。
据初步统计，目前全国各类自然保护地超过1万个，覆盖了我国陆地面积的18%左右。我国最典型的森林、草原、湿地、荒漠生态系统，最濒危最珍贵的野生动植物资源，有突出价值的自然遗产都集中在自然保护地中。（记者 寇江泽）', '2018-06-01 09:23:51.000', '2018-06-01 09:23:51.000', NULL, NULL, 'http://finance.china.com.cn/roll/20180601/4655999.shtml', '6月起 全国自然保护地大检查将展开', '2018-06-01 09:23:47.0000000', '中国财经网', '2018-06-01 08:56:00.0000000', NULL, '10177021264857711065', 0, 1, 'news_spider', -1, NULL, -1, -1, NULL, 0, NULL, NULL, -1, 1, 22812789, '2018-06-01 09:23:50.0000000', '财经>滚动新闻', 0x003FF5923F);
