INSERT INTO "news_detail_backup" ("news_id", "news_body", "insert_time", "update_time", "source_name", "news_author", "news_url", "news_title", "news_fetch_time", "news_site_name", "news_publish_time", "news_comments_num", "hashValue", "has_summary", "isActive", "source_type", "news_tag", "related_company_id", "main_related_company_id", "event_id", "neg_pos", "duplicate_num", "kpi_tag", "comments_num", "kpi_type", "usefull", "group_id", "auto_update_time", "page_tag", "TMSTAMP") VALUES (45573070, '中国网财经6月1日讯 近日多家新三板上市公司发布公告，公司增发方案获董事会及股东大会批准。据中国网财经记者统计，本披增发募资的上市公司中，欣隆环保募资额为3760万元，中科水生募资额为1.20亿元，力好科技募资额为1.08亿元，博远欣绿募资额为5000万元，美灵宝募资额为6000万元，铸金股份募资额为4995万元，鑫航科技募资额为3500万元，力源环保募资额为1560万元，8家公司共计募集资金4.76亿元。
